div.script:
$(function() {
    $("#datepicker").datepicker({
        minDate : new Date($('#bk_datepicker').data('bk_default_date')),
        maxDate : new Date($('#bk_datepicker').data('bk_end_date')),
        beforeShowDay: function(date){
        var closing_days = $('#bk_datepicker').data('closing-day');
        var month = date.getMonth();
        month = parseInt(month) + 1 ;
        var string_to_add = '-';
        if(month.toString().length == 1)
            string_to_add+= 0;
        var new_date = date.getFullYear()+string_to_add+ month+'-';
        if(date.getDate() < 10)
            new_date += '0'+date.getDate();
        else
            new_date += date.getDate();
        if($('#bk_datepicker').data('w_c_days').indexOf(date.getDay()) != -1 ){
                                    return [false, ''];
                                }
        if( closing_days.includes(new_date)){
            return [false, ''];
        }
        return [true, ''];
        }
        });
});
